const timenow = Date.now();
const topic_time = msg.topic;
let topicTimes = global.get("topicTimes") || {};

const time = topicTimes[topic_time];

if (!time || time === 0 || time > timenow) {
    topicTimes[topic_time] = timenow;
    global.set("topicTimes", topicTimes);
    return msg;
}

const diffInMilliseconds = timenow - time;
const diffInMinutes = diffInMilliseconds / 60000;

if (diffInMinutes > 1) {
    topicTimes[topic_time] = timenow;
    global.set("topicTimes", topicTimes);
    return msg;
}

