let data = msg.payload.data;
let html = '';

// Cores de fundo diferentes para os retângulos
let colors = [
    'red', 'green', 'blue', 'yellow', 'orange', 'purple', 'pink', 'brown'
];

data.forEach((value, index) => {
    let color = colors[index % colors.length]; // Seleciona uma cor da lista de cores
    html += `
        <div style="width:100%; height:100px; background-color:${color}; margin-bottom: 10px;">
            <p>Data ${index + 1}: ${value}</p>
        </div>
    `;
});

msg.payload = html;
return msg;
